import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { Supplier } from 'src/app/shared/supplier';

@Component({
  selector: 'app-suppliermodal',
  templateUrl: './suppliermodal.component.html',
  styleUrls: ['./suppliermodal.component.css']

})
export class SuppliermodalComponent {
  selectedSupplier: Supplier | null = null;
  suppliers: Supplier[] = []; // Provide the list of suppliers here

  constructor(
    public dialogRef: MatDialogRef<SuppliermodalComponent>,
    @Inject(MAT_DIALOG_DATA) public data: Supplier[]
  ) {}

  // Function to handle when a supplier is selected
  selectSupplier(event: any) {
    this.selectedSupplier = event.value;
  }

  // Add a getter to check if a supplier is selected or not
  isSupplierSelected(): boolean {
    return this.selectedSupplier !== null;
  }

  confirmSelection() {
    this.dialogRef.close(this.selectedSupplier); // Close the modal and pass the selected supplier back to the parent component
  }

  // Function to handle when the modal is closed without selecting a supplier
  cancelSelection() {
    this.dialogRef.close(); // Close the modal without passing any data back to the parent component
  }
}
