import { Component, OnInit, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';
import { EmployeeService } from '../services/employee.service';
import { Employee } from '../shared/employee';



@Component({
  selector: 'app-view-employees',
  templateUrl: './view-employees.component.html',
  styleUrls: ['./view-employees.component.css']
})
export class ViewEmployeesComponent {
  employees:Employee[] = []

  constructor(private employeeservice: EmployeeService, private router: Router){}

  ngOnInit(): void{
    this.getAllEmployees()
  }

  getAllEmployees()
  {
    this.employeeservice.getAllEmployees().subscribe(result => {
      let employeeList:any[] = result
      employeeList.forEach((element) => {
        this.employees.push(element)
      });
    })
  }

  DeleteEmployee(EmployeeId: Number){
    this.employeeservice.DeleteEmployee(EmployeeId).subscribe(result => {
      window.location.reload();
      });
    }

    searchTerm: string = '';

    @Output() searchClicked: EventEmitter<string> = new EventEmitter<string>();

    search(searchTerm: string) {
      this.searchClicked.emit(searchTerm);
    }


}
<h3 style="text-align: center;">Employee Listing </h3>
<div class="input-group">
    <input type="text" class="form-control" placeholder="Search" [(ngModel)]="searchTerm">
        <button class="btn" (click)="search(searchTerm)">
            <i class="fa fa-search" aria-hidden="true"></i>
          </button>
  </div>

<table class="table table-striped table-hover"> 
    <thead class="thead-dark">
        <tr> 
            <th scope="col">ID</th> 
            <th scope="col">Surname</th> 
            <th scope="col">First Name</th>
            <th scope="col">Email</th>
            <th scope="col">Address</th>
            <th scope="col">Phone Number</th>
            <th scope="col"></th>
            </tr> 
    </thead> 
    <tbody> 
        <tr *ngFor="let employee of employees"> 
            <td>{{employee.EmployeeId}}</td> 
            <td>{{employee.Surname}}</td> 
            <td>{{employee.FirstName}}</td> 
            <td>{{employee.Email_Address}}</td> 
            <td>{{employee.Physical_Address}}</td> 
            <td>{{employee.PhoneNumber}}</td> 
            <td>
                <button style="margin-right: 1em;" [routerLink]="['/view-employee', employee.EmployeeId]" type="button" class="btn btn-sm btn-primary" ><i class="fa fa-pencil" aria-hidden="true"></i> Edit</button>
                <button type="button" class="btn btn-sm btn-danger" (click)="DeleteEmployee(employee.EmployeeId)" ><i class="fa fa-trash" aria-hidden="true"></i> Delete</button>

            </td>
        </tr> 
    </tbody>  

