import { Component } from '@angular/core';
import { EmployeeService } from '../../services/employee.service';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-edit-employee',
  templateUrl: './edit-employee.component.html',
  styleUrls: ['./edit-employee.component.css']
})
export class EditEmployeeComponent {

  employeeForm = new FormGroup(
    {
        Surname: new FormControl(''),
        FirstName: new FormControl(''),
        Email_Address: new FormControl(''),
        Physical_Address: new FormControl(''),
        PhoneNumber: new FormControl('')
    })

  employee:any
  constructor(private employeeservice: EmployeeService, private router: Router, private route:ActivatedRoute) { }

  ngOnInit(): void {
    this.employeeservice.getEmployee(+this.route.snapshot.params['Id']).subscribe(result => {
      this.employee = result
      this.employeeForm.patchValue({
        Surname: this.employee.Surname,
        FirstName: this.employee.FirstName,
        Email_Address: this.employee.Email_Address,
        Physical_Address: this.employee.Physical_Address,
        PhoneNumber: this.employee.PhoneNumber
      });
  })
  }


  cancel(){
    this.router.navigate(['/view-employees'])
  }

  onSubmit(){
    this.employeeservice.editEmployee(this.employee.employeeId, this.employeeForm.value).subscribe(result => {
          this.router.navigate(['/view-employees'])
    })
  }


}
