import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { MatSnackBar, MatSnackBarRef } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { SupplierService } from 'src/app/service/supplier.service';
import { SupplierType } from 'src/app/shared/SupplierTypes';
import { Supplier } from 'src/app/shared/supplier';

@Component({
  selector: 'app-add-supplier',
  templateUrl: './add-supplier.component.html',
  styleUrls: ['./add-supplier.component.css']
})
export class AddSupplierComponent implements OnInit{

   supplierTypesData:SupplierType[]=[]
   toastContainer: any;

   constructor(private  supplierservice: SupplierService, private router: Router, private dialog: MatDialog, private snackBar: MatSnackBar) { }

   supplierForm: FormGroup = new FormGroup({
     supplierName: new FormControl('',[Validators.required]),
     supplierType: new FormControl([Validators.required]),
     email_Address: new FormControl('',[Validators.required]),
     physical_Address: new FormControl('',[Validators.required]),
     phoneNumber: new FormControl('',[Validators.required])

   })

   ngOnInit(): void {
     this.GetAllSupplierTypes()
   }

   cancel(){
     this.router.navigate(['/view-suppliers'])
   }

   GetAllSupplierTypes(){
     this.supplierservice.GetAllSupplierTypes().subscribe(result => {
       let supplierTypeList:any[] = result
       supplierTypeList.forEach((element) => {
         this.supplierTypesData.push(element)
      });
     });
   }

   onSubmit() {
    if (this.supplierForm.invalid) {
      return;
    }

    let supplier = new Supplier();
    supplier.supplierName = this.supplierForm.value.supplierName;
    supplier.supplierType = this.supplierForm.value.supplierType;
    supplier.email_Address = this.supplierForm.value.email_Address;
    supplier.physical_Address = this.supplierForm.value.physical_Address;
    supplier.phoneNumber = this.supplierForm.value.phoneNumber;

    this.supplierservice.AddSupplier(supplier).subscribe(result => {
      this.router.navigate(['/view-suppliers'])
});

    this.snackBar.open(
      this.supplierForm.get('name')!.value + ` created successfully`,
      'X',
      { duration: 5000 }
    );
  }




}

<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-2">
  <h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Add Supplier </h4>
  <hr>
  <div class="container">
  <div class="row">  
  <form [formGroup]="supplierForm" class="form-horizontal"  (ngSubmit)="onSubmit()">

      <div class="form-group">
          <label class=" col-md-1" style="font-weight: bold;" for="supplierName">Name:</label>
          <div class="col-md-6">
            <input class="form-control" placeholder="Enter Supplier Name" [required]="true" name="supplierName" formControlName="supplierName">
          </div>
      </div>

      <div class="form-group">
        <label class="col-md-1" style="font-weight: bold;" for="supplierType">Supplier Type</label>
        <div class="col-md-6">
        <select class="form-control" id="supplierType" formControlName="supplierType">
          <option *ngFor="let item of supplierTypesData" [value]="item.supplier_TypeId">{{ item.name }}</option>
        </select>
      </div>
      </div>


      <div class="form-group">
        <span>
          <label class="col-sm-1" style="font-weight: bold;" for="email_Address">Email Address</label>
          <div class="col-sm-6">
            <input class="form-control"  placeholder="xxxxx@gmail.com" [required]="true" name="email_Address" formControlName="email_Address">
          </div>
        </span>
      </div>

      <div class="form-group">
          <label class="col-sm-2" style="font-weight: bold;" for="phoneNumber">Phone Number:</label>
          <div class="col-sm-6">
            <input class="form-control" type="text"  placeholder="Enter 10-digit number" [required]="true" name="phoneNumber" formControlName="phoneNumber">
          </div>
      </div>


    <div class="form-group">
        <label class="col-sm-1" style="font-weight: bold;" for="physical_Address">Physical Address:</label>
        <div class="col-sm-6">
          <input class="form-control" type="text" id="physical_Address" placeholder="Enter Address" name="physical_Address" [required]="true" formControlName="physical_Address">
        </div>
    </div>

      <div class="form-group">
        <div class="col-sm-offset-1 col-sm-6">
          <button style="margin-right:1em;" type="submit" [disabled]="!supplierForm.valid"  class="btn btn-sm btn-success">Add</button>
          <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Cancel</button>
        </div>
      </div>
    </form>
    </div>
  </div> 
    </div>
    </div>
  </div>
