import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { InventoryService } from 'src/app/service/inventory.service';
import { InventoryItem } from 'src/app/shared/inventoryitem';
import { InventoryType } from 'src/app/shared/inventorytype';

@Component({
  selector: 'app-add-inventoryitem',
  templateUrl: './add-inventoryitem.component.html',
  styleUrls: ['./add-inventoryitem.component.css']
})
export class AddInventoryitemComponent {
  inventoryTypesData:InventoryType[]=[]
   toastContainer: any;

   constructor(private  inventoryservice: InventoryService, private router: Router, private dialog: MatDialog, private snackBar: MatSnackBar) { }

   inventoryItemForm: FormGroup = new FormGroup({
     itemName: new FormControl('',[Validators.required]),
     inventoryType: new FormControl([Validators.required]),
     description: new FormControl('',[Validators.required])
   })

   ngOnInit(): void {
     this.GetAllInventoryTypes()
   }

   cancel(){
     this.router.navigate(['/selected-inventorytype'])
   }

   GetAllInventoryTypes(){
     this.inventoryservice.GetAllInventoryTypes().subscribe(result => {
       let inventoryTypeList:any[] = result
       inventoryTypeList.forEach((element) => {
         this.inventoryTypesData.push(element)
      });
     });
   }

   onSubmit() {
    if (this.inventoryItemForm.invalid) {
      return;
    }

    let inventoryitem = new InventoryItem();
    inventoryitem.itemName = this.inventoryItemForm.value.itemName;
    inventoryitem.inventoryType = this.inventoryItemForm.value.inventoryType;
    inventoryitem.description = this.inventoryItemForm.value.description;


    this.inventoryservice.AddInventoryItem(inventoryitem).subscribe(result => {
      this.router.navigate(['/view-inventoryitems/:typeId'])
});

    this.snackBar.open(
      this.inventoryItemForm.get('itemName')!.value + ` created successfully`,
      'X',
      { duration: 5000 }
    );
  }

}

<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-2">
  <h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Add Supplier </h4>
  <hr>
  <div class="container">
  <div class="row">  
  <form [formGroup]="inventoryItemForm" class="form-horizontal"  (ngSubmit)="onSubmit()">

      <div class="form-group">
          <label class=" col-md-1" style="font-weight: bold;" for="itemName">Name:</label>
          <div class="col-md-6">
            <input class="form-control" placeholder="Enter Inventory Name" [required]="true" name="itemName" formControlName="itemName">
          </div>
      </div>

      <div class="form-group">
        <label class="col-md-1" style="font-weight: bold;" for="inventoryType">Inventory Type</label>
        <div class="col-md-6">
        <select class="form-control" id="inventoryType" formControlName="inventoryType">
          <option *ngFor="let item of inventoryTypesData" [value]="item.inventory_TypeId">{{ item.name }}</option>
        </select>
      </div>
      </div>


      <div class="form-group">
        <span>
          <label class="col-sm-1" style="font-weight: bold;" for="description">Description</label>
          <div class="col-sm-6">
            <input class="form-control"  placeholder="Description" [required]="true" name="description" formControlName="description">
          </div>
        </span>
      </div>

      <div class="form-group">
        <div class="col-sm-offset-1 col-sm-6">
          <button style="margin-right:1em;" type="submit" [disabled]="!inventoryItemForm.valid"  class="btn btn-sm btn-success">Add</button>
          <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Cancel</button>
        </div>
      </div>
    </form>
    </div>
  </div> 
    </div>
    </div>
  </div>
