import { Component, ViewChild, OnInit } from '@angular/core';
import { EventInput } from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid'; // Import the dayGrid plugin
import interactionPlugin from '@fullcalendar/interaction';
import { Schedule } from 'src/app/shared/schedule';


@Component({
  selector: 'app-schedule-display',
  templateUrl: './schedule-display.component.html',
  styleUrls: ['./schedule-display.component.css']
})

export class ScheduleDisplayComponent implements OnInit {

  @ViewChild('fullcalendar') fullcalendar: any; // Get a reference to the calendar element
  calendarPlugins = [dayGridPlugin, interactionPlugin];

  calendarEvents: Schedule[] = [];
  ngOnInit() {
    // Fetch events from a service or local storage and populate the calendarEvents array
    // For demonstration purposes, let's add a sample event:
    this.calendarEvents = [
      {
        scheduleId: 1,
        event_name: 'Sample Event',
        date: new Date('2023-07-26'),
        eventId:2,
        start_time: '2023-07-25T10:00:00',
        end_time: '2023-07-25T12:00:00',
      }
    ];
  }

   // Handler for clicking on a day
   dateClick(event: any) {
    // Open a dialog or a form to add a new event with the selected date (event.date)
  }

  // Handler for event click
  eventClick(eventInfo: any) {
    const event = eventInfo.event;
    // Open a dialog or a form to edit or delete the clicked event
  }
}
<h3 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-weight: bolder; font-size: 50px;">Schedule</h3>

<div class="container text-center">

<full-calendar #calendar
[plugins]="calendarPlugins"
[weekends]="true"
[editable]="true"
[events]="events"
(eventClick)="handleEventClick($event)"></full-calendar>
</div>
