import { Component } from '@angular/core';
import { EmployeeService } from '../../../../service/employee.service';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Employee } from '../../../../shared/employee';
import { MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar, MatSnackBarRef } from '@angular/material/snack-bar';


@Component({
  selector: 'app-edit-employee',
  templateUrl: './edit-employee.component.html',
  styleUrls: ['./edit-employee.component.css']
})
export class EditEmployeeComponent {
  constructor(
    private employeeservice:EmployeeService, 
    private router : Router , 
    private activated:ActivatedRoute,
    private dialog: MatDialog,
    private snackBar: MatSnackBar) { }

  editEmployee: Employee = new Employee();

  updateEmployeeForm: FormGroup = new FormGroup({
    surname: new FormControl('',[Validators.required]),
    firstName: new FormControl('',[Validators.required]),
    email_Address: new FormControl('',[Validators.required]),
    physical_Address: new FormControl('',[Validators.required]),
    phoneNumber: new FormControl('',[Validators.required])
  })
  

  ngOnInit(): void {

    this.activated.params.subscribe(params => { 
      this.employeeservice.getEmployee(params['id']).subscribe(res => { 
      this.editEmployee = res as Employee;

      this.updateEmployeeForm.controls['surname'].setValue(this.editEmployee.surname);
      this.updateEmployeeForm.controls['firstName'].setValue(this.editEmployee.firstName);
      this.updateEmployeeForm.controls['email_Address'].setValue(this.editEmployee.email_Address);
      this.updateEmployeeForm.controls['physical_Address'].setValue(this.editEmployee.physical_Address);
      this.updateEmployeeForm.controls['phoneNumber'].setValue(this.editEmployee.phoneNumber);
      })
 
     })
  }

  cancel(){
    this.router.navigate(['/view-employees'])
  }

  updateEmployee()
  {
    let employee = new Employee();
    employee.surname = this.updateEmployeeForm.value.surname;
    employee.firstName = this.updateEmployeeForm.value.firstName;
    employee.email_Address = this.updateEmployeeForm.value.email_Address;
    employee.physical_Address = this.updateEmployeeForm.value.physical_Address;
    employee.phoneNumber = this.updateEmployeeForm.value.phoneNumber;


   this.employeeservice.EditEmployee(this.editEmployee.employeeId,employee).subscribe((response:any) => {

    if(response.statusCode == 200)
    {
      this.router.navigate(['/view-employees'])
      window.location.reload();
    }
    else
    {

    }
   });

   this.showSuccessMessage('Employee Informartion updated successfully!');

  }
  showSuccessMessage(message: string): void {
    const snackBarRef: MatSnackBarRef<any> = this.snackBar.open(message, 'Ok', {
      duration: 3000, // Duration in milliseconds
      horizontalPosition: 'center',
      verticalPosition: 'bottom'
    });
  }
}



<div class="container-fluid">
  <div class="row">
      <div class="col-md-2">
          <app-navbar></app-navbar>
      </div>
  <div class="col-md-10 order-md-1">
<h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Edit Employee</h4>
<hr>
<div class="container">
<div class="row">  
<form [formGroup]="updateEmployeeForm" class="form-horizontal"  (ngSubmit)="updateEmployee()">
    <div class="form-group">
        <label class=" col-sm-1" style="font-weight: bold;" for="surname">Surname:</label>
        <div class="col-sm-6">
          <input class="form-control" id="surname" placeholder="Enter Surname" [required]="true" formControlName="surname">
        </div>
    </div>
    <div class="form-group">
        <label class="col-sm-1" style="font-weight: bold;" for="firstName">FirstName:</label>
        <div class="col-sm-6">
          <input class="form-control" id="firstName" placeholder="Enter First Name" [required]="true" formControlName="firstName">
        </div>
    </div>
    <div class="form-group">
      <label class="col-sm-1" style="font-weight: bold;" for="email_Address">Email Address:</label>
      <div class="col-sm-6">
        <textarea class="form-control" type="text" id="email_Address" placeholder="Enter Address" [required]="true" formControlName="email_Address"></textarea>
      </div>
  </div>
    <div class="form-group">
        <label class="col-sm-1" style="font-weight: bold;" for="physical_Address">Address:</label>
        <div class="col-sm-6">
          <textarea class="form-control" type="text" id="physical_Address" placeholder="Enter Address" [required]="true" formControlName="physical_Address"></textarea>
        </div>
    </div>
    <div class="form-group">
        <label class="col-sm-1" style="font-weight: bold;" for="phoneNumber">Email:</label>
        <div class="col-sm-6">
          <textarea class="form-control" type="text" id="phoneNumber" placeholder="Enter 10-digit number" [required]="true" formControlName="phoneNumber"></textarea>
        </div>
    </div>
    <div class="form-group">
      <div class="col-sm-offset-1 col-sm-6">
        <button style="margin-right:1em;" type="submit" [disabled]="!updateEmployeeForm.valid" class="btn btn-sm btn-success">Confirm</button>
        <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Cancel</button>
      </div>
    </div>
  </form>
  </div>
</div> 
  </div>
  </div>
</div>
