formData = new FormData();
supplierTypesData:SupplierType[]=[]

supplierForm: FormGroup = this.fb.group({
 name: ['', Validators.required],
 supplierType: [null, Validators.required],
 email_Address: ['', Validators.required],
 phoneNumber: ['', Validators.required],
 physical_Address: ['', Validators.required]

})

constructor(private supplierservice: SupplierService, private fb: FormBuilder, private router: Router, private snackBar: MatSnackBar) { }

ngOnInit(): void {

 this.GetAllSupplierTypes()
}

GetAllSupplierTypes(){
this.supplierservice.GetAllSupplierTypes().subscribe(result => {
 let brandList:any[] = result
 brandList.forEach((element) => {
   this.supplierTypesData.push(element)
 });
});
}

onSubmit() {
 if(this.supplierForm.valid)
 {
   this.formData.append('name', this.supplierForm.get('name')!.value);
   this.formData.append('supplierType', this.supplierForm.get('supplierType')!.value);
   this.formData.append('email_Address', this.supplierForm.get('email_Address')!.value);
   this.formData.append('phoneNumber', this.supplierForm.get('phoneNumber')!.value);
   this.formData.append('physical_Address', this.supplierForm.get('physical_Address')!.value);

   this.supplierservice.AddSupplier(this.formData).subscribe(() => {
     this.clearData()
     this.router.navigateByUrl('view-suppliers').then((navigated: boolean) => {
       if(navigated) {
         this.snackBar.open(this.supplierForm.get('name')!.value + ` created successfully`, 'X', {duration: 5000});
       }
    });
   });
 }
}

clearData(){
 this.formData.delete("name");
 this.formData.delete("supplierType");
 this.formData.delete("email_Address");
 this.formData.delete("phoneNumber");
 this.formData.delete("physical_Address");
}

}

<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-2">
  <h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Add Supplier </h4>
  <hr>
  <div class="container">
  <div class="row">  
  <form [formGroup]="supplierForm" class="form-horizontal"  (ngSubmit)="onSubmit()">

      <div class="form-group">
          <label class=" col-md-1" style="font-weight: bold;" for="name">Name:</label>
          <div class="col-md-6">
            <input class="form-control" placeholder="Enter Supplier Name" [required]="true" name="name" formControlName="name">
          </div>
      </div>

      <div class="form-group">
        <label class="col-md-1" style="font-weight: bold;" for="supplierTypeName">Supplier Type</label>
        <div class="col-md-6">
        <select class="form-control" id="supplierTypeName" name="supplierTypeName">
          <option *ngFor="let item of supplierTypesData" [value]="item.supplier_TypeId">{{ item.name }}</option>
        </select>
      </div>
      </div>


      <div class="form-group">
        <span>
          <label class="col-sm-1" style="font-weight: bold;" for="email_Address">Email Address</label>
          <div class="col-sm-6">
            <input class="form-control"  placeholder="xxxxx@gmail.com" [required]="true" name="email_Address" formControlName="email_Address">
          </div>
        </span>
      </div>

      <div class="form-group">
          <label class="col-sm-2" style="font-weight: bold;" for="phoneNumber">Phone Number:</label>
          <div class="col-sm-6">
            <input class="form-control" type="text"  placeholder="Enter 10-digit number" [required]="true" name="phoneNumber" formControlName="phoneNumber">
          </div>
      </div>


    <div class="form-group">
        <label class="col-sm-1" style="font-weight: bold;" for="physical_Address">Physical Address:</label>
        <div class="col-sm-6">
          <input class="form-control" type="text" id="physical_Address" placeholder="Enter Address" name="physical_Address" [required]="true" formControlName="physical_Address">
        </div>
    </div>

      <div class="form-group">
        <div class="col-sm-offset-1 col-sm-6">
          <button style="margin-right:1em;" type="submit" [disabled]="!supplierForm.valid"  class="btn btn-sm btn-success">Add</button>
          <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Cancel</button>
        </div>
      </div>
    </form>
    </div>
  </div> 
    </div>
    </div>
  </div>
