   <div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-1">
    <h3 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px; font-weight: bolder;">Employee Listing </h3>
    <!-- <div class="input-group">
        <input type="text" class="form-control" placeholder="Search"> 
        <button class="btn">
            <i class="fa fa-search" aria-hidden="true"></i>
          </button>
      </div>  -->
      <label>Filter</label>
      <input type="text" class="form-control" (input)="applyFilter($event)" placeholder="Search Columns">

    <table class="table table-striped table-hover table-container outlined-table">
        <thead class="tableColor">

                <th scope="col">ID</th>
                <th scope="col">Name</th>
                <th scope="col">Type</th>
                <th scope="col">Email</th>
                <th scope="col">Cell</th>
                <th scope="col">Address</th>
                <th scope="col"></th>
                <th scope="col"></th>
        </thead>
        <tbody>
          <tr *ngFor="let supplier of filteredsuppliers">
                <td>{{supplier.supplierId}}</td>
                <td>{{supplier.name}}</td>
                <td>{{supplier.supplierTypeName}}</td>
                <td>{{supplier.email_Address}}</td>
                <td>{{supplier.phoneNumber}}</td>
                <td>{{supplier.physical_Address}}</td>

                  <td> <i class="fa fa-pencil" [routerLink]="['/edit-supplier', supplier.supplierId]" aria-hidden="true"></i></td>
                  <td><i class="fa fa-trash"  (click)="DeleteSupplier(supplier.supplierId)" aria-hidden="true"></i></td>
            </tr>
        </tbody>
    </table>
    </div>
    </div>



import { HttpClient } from '@angular/common/http';
import { Component, ViewChild } from '@angular/core';
import { MatPaginator } from '@angular/material/paginator';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatSort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
import { SupplierService } from 'src/app/service/supplier.service';
import { Supplier } from 'src/app/shared/supplier';
import { Router } from '@angular/router';

@Component({
  selector: 'app-view-suppliers',
  templateUrl: './view-suppliers.component.html',
  template: `
  <button (click)="deleteItem()">Delete</button>
  <button (click)="cancel()">Cancel</button>
`,
  styleUrls: ['./view-suppliers.component.css']
})
export class ViewSuppliersComponent {

  suppliers: Supplier[] = []
  filteredsuppliers: Supplier[] = [];

  constructor(private supplierservice: SupplierService, private snackBar: MatSnackBar, private httpClient: HttpClient, private router: Router){}

  deleteItem(): void {
    const confirmationSnackBar = this.snackBar.open('Are you sure you want to delete this supplier?', 'Delete, Cancel',{
      duration: 5000, // Display duration in milliseconds

    });


    //  cancel(){
    //    this.router.navigate(['/home'])
    //  }


    confirmationSnackBar.onAction().subscribe(() => {
      // Perform the deletion action here
      this.deleteItemFromServer();
      window.location.reload();
    });
  }

deleteItemFromServer(): void {
  this.DeleteSupplier;
}

  ngOnInit(): void {
    this.GetAllSuppliers()
    console.log(this.suppliers)

    this.filteredsuppliers = this.suppliers
    console.log(this.filteredsuppliers)

  }

  GetAllSuppliers()
  {
    this.supplierservice.GetAllSuppliers().subscribe(result => {
      let supplierList:any[] = result
      supplierList.forEach((element) => {
        this.suppliers.push(element)

      });
    })
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value.toLowerCase();

    this.filteredsuppliers = this.suppliers.filter(supplier => {
      const column2Value = supplier.name.toLowerCase() || supplier.name.toUpperCase();
      const column3Value = supplier.supplierTypeName.toLowerCase();

      return column2Value.includes(filterValue) || column3Value.includes(filterValue);
    });
  }


  DeleteSupplier(supplierId: Number){
    this.supplierservice.DeleteSupplier(supplierId).subscribe(result => {
      this.deleteItem();
      });
    }

}
