import { Component,OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from 'src/app/service/data.Service';
import { Employee_Role } from 'src/app/shared/EmployeeRole';

@Component({
  selector: 'app-edit-employee-role',
  templateUrl: './edit-employee-role.component.html',
  styleUrls: ['./edit-employee-role.component.css']
})
export class EditEmployeeRoleComponent implements OnInit{

 
constructor(private dataService: DataService, 
  private router: Router, 
  private activated:ActivatedRoute) { }

  editEmployeeRole: Employee_Role = new Employee_Role();

  updateEmployeeRoleForm: FormGroup = new FormGroup({
     name: new FormControl('',[Validators.required]),
     description: new FormControl('',[Validators.required])
  })

  ngOnInit(): void {
    this.activated.params.subscribe(params =>{
    this.dataService.GetEmployeeRole(params['id']).subscribe(result =>{
      this.editEmployeeRole = result as Employee_Role;
      this.updateEmployeeRoleForm.controls['name'].setValue(this.editEmployeeRole.name);
      this.updateEmployeeRoleForm.controls['description'].setValue(this.editEmployeeRole.description);
    })
    })

  }

  cancel(){
    this.router.navigate(['/employee-role'])
  }

  onSubmit(){
    let employeeRole = new Employee_Role();
    employeeRole.name = this.updateEmployeeRoleForm.value.name;
    employeeRole.description = this.updateEmployeeRoleForm.value.description;

    this.dataService.EditEmployeeRole(this.editEmployeeRole.employee_RoleId,employeeRole).subscribe((result:any) => {

    this.router.navigate(['employee-role'])
    });
  }

}
<div class="container-fluid">
  <div class="row">
      <div class="col-md-2">
          <app-navbar></app-navbar>
      </div>
  <div class="col-md-10 order-md-1">
<h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Update Employee Role</h4>
<hr>
<div class="container">
<div class="row">  
<form [formGroup]="updateEmployeeRoleForm" class="form-horizontal"  (ngSubmit)="onSubmit()">

    <div class="form-group">
        <label class=" col-sm-1" style="font-weight: bold;" for="name">Name:</label>
        <div class="col-sm-6">
          <input class="form-control" id="name" placeholder="Enter name" [required]="true" formControlName="name">
        </div>
    </div>
    <div class="form-group">
        <label class="col-sm-1" style="font-weight: bold;" for="description">Description:</label>
        <div class="col-sm-6">
          <textarea class="form-control" type="text" id="description" placeholder="Enter description" [required]="true" formControlName="description"></textarea>
        </div>
    </div>
    <br>
    <div class="form-group">
      <div class="col-sm-offset-1 col-sm-6">
        <button style="margin-right:1em;" type="submit" [disabled]="!updateEmployeeRoleForm.valid" class="btn btn-sm btn-success" (click)="onSubmit()">Update</button>
        <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Cancel</button>
      </div>
    </div>
  </form>
  </div>
</div>
  </div>
  </div>
</div>
