import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { MatSnackBar } from '@angular/material/snack-bar';
import { DataService } from 'src/app/service/data.Service';

@Component({
  selector: 'app-reset-password',
  templateUrl: './reset-password.component.html',
  styleUrls: ['./reset-password.component.css']
})
export class ResetPasswordComponent {
  hide = true;
  loginFormGroup: FormGroup = this.fb.group({
    NewPassword: ['', [Validators.required, Validators.minLength(8), Validators.maxLength(16)]],
    ConfirmPassword: ['', [Validators.required, Validators.minLength(8), Validators.maxLength(16)]],
  })

  isLoading:boolean = false

  constructor(private router: Router, 
    private dataService: DataService, 
    private fb: FormBuilder,  
    private snackBar: MatSnackBar) { }

  ngOnInit(): void {
  }

  Reset(){

  }
}


<mat-toolbar style=" background-color: rgb(165, 113, 207); ;" class="center">
  <img src="../../assets/Nav_image/Africanacity.png" alt="" width="40" height="40" style="margin-left: 50%;"class="d-inline-block align-text-top">
  Africanacity
</mat-toolbar>
<div class="container" >
    <mat-card class="box" *ngIf="!isLoading">
     
      <mat-card-header>
        <mat-card-title> Reset Password </mat-card-title>
        <img mat-card-avatar src="../../assets/Nav_image/Africanacity.png" alt="" width="40" height="40" class="d-inline-block align-text-top">
      </mat-card-header>
      <form class="form" [formGroup]="loginFormGroup">
        <mat-card-content>
          <mat-form-field class="full-width">
            <mat-label>New Password </mat-label>
            <input type="password" matInput placeholder="Enter your new password" formControlName="Password">
          </mat-form-field><br>
          <mat-form-field class="full-width">
            <mat-label>Confirm Password </mat-label>
            <input type="password" matInput placeholder="Enter your password" formControlName="Password">
            <button mat-icon-button matSuffix (click)="hide = !hide" [attr.aria-label]="'Hide password'" [attr.aria-pressed]="hide">
              <mat-icon>{{hide ? 'visibility_off' : 'visibility'}}</mat-icon>
              </button>
          </mat-form-field>
        </mat-card-content>
        <mat-card-actions>
        <button mat-raised-button color="primary" (click)="Reset()"> Submit </button>
        <button mat-raised-button color="warn" (click)="Reset()"> Cancel </button><br>
        </mat-card-actions>
        
      </form>
    </mat-card>
    <mat-progress-spinner mode="indeterminate" value="50" *ngIf="isLoading">
    </mat-progress-spinner>
  </div>
