import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { DataService } from 'src/app/service/data.Service';

@Component({
  selector: 'app-update-password',
  templateUrl: './update-password.component.html',
  styleUrls: ['./update-password.component.css']
})

export class UpdatePasswordComponent {
  updatePasswordFormGroup: FormGroup = this.fb.group({
    CurrentPassword: ['', [Validators.required, Validators.minLength(8), Validators.maxLength(16)]],
    NewPassword: ['', [Validators.required, Validators.minLength(8), Validators.maxLength(16)]],
    ConfirmPassword: ['', [Validators.required, Validators.minLength(8), Validators.maxLength(16)]],
  })

  constructor(private router: Router, private dataService: DataService, private fb: FormBuilder, private snackBar: MatSnackBar) { }

  ngOnInit(): void {
  }

  ChangePassword(){

    if(this.updatePasswordFormGroup.valid)
    {
      this.dataService.RegisterUser(this.updatePasswordFormGroup.value).subscribe(() => {
        this.updatePasswordFormGroup.reset();
        this.router.navigate(['login']).then((navigated: boolean) => {
          if(navigated) {
            this.snackBar.open(`Password successfully updated`, 'X', {duration: 5000});
          }
       });
      }, (response: HttpErrorResponse) => {
        if (response.status === 403) {
          this.snackBar.open(response.error, 'X', {duration: 5000});
        }
        if (response.status === 500){
          this.snackBar.open(response.error, 'X', {duration: 5000});
        }
      })
    }
  }
}
<mat-toolbar style=" background-color: rgb(165, 113, 207); ;" class="center">
  <img src="../../assets/Nav_image/Africanacity.png" alt="" width="40" height="40" style="margin-left: 50%;"class="d-inline-block align-text-top">
  Africanacity
</mat-toolbar>
<div class="login-wrapper container" fxLayout="row" fxLayoutAlign="center center">
      <mat-card class="box">
        <mat-card-header>
          <img mat-card-avatar src="../../assets/Nav_image/Africanacity.png" alt="" width="40" height="40" class="d-inline-block align-text-top">
          <mat-card-title>Change Password</mat-card-title>
        </mat-card-header>
        <form class="form" [formGroup]="updatePasswordFormGroup">
          <mat-card-content>
          <mat-form-field class="full-width" floatLabel="always">
              <mat-label>Current Password</mat-label>
            <input matInput placeholder="Enter your password" formControlName="PhoneNumber">
          </mat-form-field><br>
            <mat-form-field class="full-width">
                <mat-label>New Password</mat-label>
              <input type="password" matInput placeholder="Enter between 8 to 16 characters" formControlName="Password">
            </mat-form-field><br>
            <mat-form-field class="full-width">
              <mat-label>Confirm Password</mat-label>
            <input type="password" matInput placeholder="Enter between 8 to 16 characters" formControlName="ConfirmPassword">
          </mat-form-field>
          </mat-card-content>
          <button mat-raised-button color="primary" class="btn-block"  (click)="ChangePassword()">Submit</button>
          <button mat-raised-button color="warn" class="btn-block" routerLink="../login">Cancel</button>
        </form>
      </mat-card>
    </div>
