import { Component, ViewChild, OnInit } from '@angular/core';
import { CalendarOptions } from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid'; // Import the dayGrid plugin
import interactionPlugin from '@fullcalendar/interaction';
import { AddScheduleComponent } from '../add-schedule/add-schedule.component';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { DataService } from 'src/app/service/data.Service';


@Component({
  selector: 'app-schedule-display',
  templateUrl: './schedule-display.component.html',
  styleUrls: ['./schedule-display.component.css']
})

export class ScheduleDisplayComponent implements OnInit {


  constructor(private modalService: NgbModal){}

   private dialogRef: any;


  ngOnInit() {


  }

  calendarOptions: CalendarOptions = {
   initialView: 'dayGridMonth',
     plugins: [dayGridPlugin, interactionPlugin],
     dateClick: this.onDateClick.bind(this),
 };

   onDateClick(date: { dateStr: string }) {
      // Open the the Bootstrap modal
      const modalRef = this.modalService.open(AddScheduleComponent, {
       size: 'sm', // Set the size of the modal if needed
       centered: true, // Center the modal on the screen
      backdrop: 'static', // Disable closing the modal by clicking outside
      });
     modalRef.componentInstance.dateStr = date.dateStr; // Pass the selected dateStr to the dialog component if needed
    }



}
<h3 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-weight: bolder; font-size: 50px;">Booking Schedule</h3>


 <div class="calendar-container">
  <full-calendar [options]="calendarOptions"></full-calendar>
</div>
