import { Component, ElementRef, ViewChild } from '@angular/core';
import { InventoryService } from 'src/app/service/inventory.service';
import { InventoryItem } from 'src/app/shared/inventoryitem';
import { EventEmitter, Output } from '@angular/core';
import { saveAs } from 'file-saver'; // Import file-saver for saving the PDF
import * as html2pdf from 'html2pdf.js';

// Import jsPDF
import jsPDF from 'jspdf';
import 'jspdf-autotable'

@Component({
  selector: 'app-checklist',
  templateUrl: './checklist.component.html',
  styleUrls: ['./checklist.component.css']
})
export class ChecklistComponent {
  checklistItems: InventoryItem[] = [];
  predefinedLevel: number = 5; // Predefined level for the quantity

  constructor(private inventoryService: InventoryService) {}

  ngOnInit() {
    this.checkInventory();
  }

  checkInventory() {
    this.inventoryService.GetAllInventoryItems().subscribe((inventoryItems: InventoryItem[]) => {
      for (let item of inventoryItems) {
        if (item.quantity < this.predefinedLevel) {
          this.addToChecklist(item);
        }
      }
    });
  }

  addToChecklist(item: InventoryItem) {
    item.isChecked = false; 
    this.checklistItems.push(item);

  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value.toLowerCase();

    this.checklistItems = this.checklistItems.filter(item => {
      const column2Value = item.itemName.toLowerCase() || item.itemName.toUpperCase();

      return column2Value.includes(filterValue);
    });
  }


  downloadItem(item: InventoryItem) {
    // Implement the logic to download the item
  }

  orderItem(item: InventoryItem) {
    // Implement the logic to order the item
  }

  queryItem(item: InventoryItem) {
    // Implement the logic to query the item
  }

  receiveItem(item: InventoryItem) {
    // Implement the logic to mark the item as received
  }


  deleteItem(item: InventoryItem) {
    // Implement the logic to mark the item as received
  }

  onCheckboxChange(item: InventoryItem) {
    if (item.isChecked) {
      // Perform any action you want when the checkbox is checked
      console.log(`Item ${item.itemName} is checked.`);
    } else {
      // Perform any action you want when the checkbox is unchecked
      console.log(`Item ${item.itemName} is unchecked.`);
    }
  }

  @ViewChild('pdfTable')
  pdfTable!: ElementRef; // Add this line to get a reference to the HTML table

  downloadPDF() {
    const doc = new jsPDF();
    const table = this.pdfTable.nativeElement;

    // Convert HTML table to PDF
    html2pdf()
      .from(table)
      .set({ margin: [20, 20] })
      .outputPdf()
      .then((pdf: any) => {
        // Save the PDF with a filename using file-saver
        const blob = pdf.output('blob');
        saveAs(blob, 'inventory_checklist.pdf');
      });
  }

}



<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-1">
    <h3 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px; font-weight: bolder;">Inventory Item Checklist </h3>

      <div class="input-group">
    <div class="search-bar">
    <input type="text" class="form-control" (input)="applyFilter($event)" placeholder="Search Columns">
    </div>
  <div class="addButton">
    <i class="fa fa-download fa-lg icon" aria-hidden="true" (click)="downloadPDF()"></i>
  </div> 
    </div>

<div class="table-container">
    <table #pdfTable class="table table-striped table-hover table-container outlined-table">
    <thead class="tableColor">
      <tr>
        <th scope="col" class="col-sm-1">ID</th>
        <th scope="col" class="col-sm-7">Name</th>
        <th scope="col" class="col-sm-1" style="text-align: center;">Query</th>
        <th scope="col" class="col-sm-1" style="text-align: center;">Delete</th>
        <th scope="col" class="col-sm-1" style="text-align: center;">Recieved</th>
        <!-- Add more columns as needed -->
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of checklistItems">
        <td class="col-sm-1">
          <input type="checkbox" [(ngModel)]="item.isChecked">
          {{ item.inventory_ItemId }}
        </td>
          <td class="col-sm-2">{{ item.itemName }}</td>

          <td class="col-sm-1" class="icon-cell">
            <i class="fa fa-question-circle fa-lg icon" aria-hidden="true" (click)="queryItem(item)"></i>
          </td>
          <td class="col-sm-1" class="icon-cell">
            <i class="fa fa-trash fa-lg icon" aria-hidden="true" (click)="deleteItem(item)"></i>
          </td>
          <td class="col-sm-1" class="icon-cell">
            <i class="fa fa-check icon" aria-hidden="true"></i>
          </td>
        <!-- Display more item details as needed -->
      </tr>
    </tbody>
  </table>
</div>
    </div>
    </div>
</div>
