   <div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-1">
    <h3 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px; font-weight: bolder;">Employee Listing </h3>
    <div class="row">
        <div class="col-md-12">
          <mat-form-field>
            <mat-label>Filter</mat-label>
            <input matInput (keyup)="applyFilter($event)" placeholder="Search Columns" #input>
          </mat-form-field>

      </div>

    <table class="table table-striped table-hover table-container outlined-table"> 
        <thead class="tableColor">

                <th scope="col">ID</th> 
                <th scope="col">Surname</th> 
                <th scope="col">First Name</th>
                <th scope="col">Email</th>
                <th scope="col">Address</th>
                <th scope="col">Phone Number</th>
                <th scope="col"></th>
        </thead> 
        <tbody> 
            <tr *ngFor="let supplier of suppliers"> 
                <td>{{supplier.supplierID}}</td> 
                <td>{{supplier.supplierName}}</td> 
                <td>{{supplier.supplierType}}</td> 
                <td>{{supplier.supplierAddress}}</td> 
                <td>{{supplier.supplierNumber}}</td> 
                <td>{{supplier.supplierAddress}}</td> 
                <td>
                    <button type="button" class="btn btn-sm btn-info"  [routerLink]="['/edit-employee']"><i class="fa fa-pencil" aria-hidden="true"></i>Edit</button>
                    <button type="button" class="btn btn-sm btn-danger" ><i class="fa fa-trash" aria-hidden="true"></i> Delete</button>
                </td>
            </tr> 
        </tbody>  
    </table>
    </div>
    </div>
