import { Component } from '@angular/core';
import { InventoryService } from 'src/app/service/inventory.service';
import { InventoryItem } from 'src/app/shared/inventoryitem';
import { EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-checklist',
  templateUrl: './checklist.component.html',
  styleUrls: ['./checklist.component.css']
})
export class ChecklistComponent {
  checklistItems: InventoryItem[] = [];
  predefinedLevel: number = 3; // Predefined level for the quantity

  constructor(public inventoryservice: InventoryService) {}

  ngOnInit() {
    // Subscribe to inventory updates or perform any other necessary initialization logic
    this.checkInventory();
  }

  checkInventory() {
    // Get the inventory items from the inventory service
    this.inventoryservice.GetAllInventoryItems().subscribe((inventoryItems: InventoryItem[]) => {
      // Iterate through each inventory item
      for (let item of inventoryItems) {
        // Check if the quantity is below the predefined level
        if (item.quantity < this.predefinedLevel) {
          // Add the item to the checklist
          this.addItemToChecklist(item);
        }
      }
    });
  }

  @Output() addItemToChecklist: EventEmitter<InventoryItem> = new EventEmitter<InventoryItem>();

  addItem(inventoryItem: InventoryItem) {
    this.addItemToChecklist.emit(inventoryItem);
  }


}


