import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { InventoryService } from 'src/app/service/inventory.service';
import { InventoryType } from 'src/app/shared/inventorytype';

@Component({
  selector: 'app-edit-intventorytype',
  templateUrl: './edit-intventorytype.component.html',
  styleUrls: ['./edit-intventorytype.component.css']
})
export class EditIntventorytypeComponent {
  constructor(private inventoryservice: InventoryService, 
    private router: Router, 
    private activated:ActivatedRoute) { }

    editInventoryType: InventoryType = new InventoryType();

    editInventoryTypeForm: FormGroup = new FormGroup({
       name: new FormControl('',[Validators.required]),
       description: new FormControl('',[Validators.required])
    })

    ngOnInit(): void {
      this.activated.params.subscribe(params =>{
      this.inventoryservice.GetInventoryType(params['id']).subscribe(result =>{
        this.editInventoryType = result as InventoryType;
        this.editInventoryTypeForm.controls['name'].setValue(this.editInventoryType.name);
        this.editInventoryTypeForm.controls['description'].setValue(this.editInventoryType.description);
      })
      })

    }

    cancel(){
      this.router.navigate(['/view-inventorytypes'])
    }

    onSubmit(){
      let inventorytype = new InventoryType();
      inventorytype.name = this.editInventoryTypeForm.value.name;
      inventorytype.description = this.editInventoryTypeForm.value.description;

      this.inventoryservice.EditInventoryType(this.editInventoryType.inventory_TypeId, inventorytype).subscribe((result:any) => {

      this.router.navigate(['view-inventorytypes'])
      });
    }

}
<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-1">
  <h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Update Inventory Type</h4>
  <hr>
  <div class="container">
  <div class="row">  
  <form [formGroup]="editInventoryTypeForm" class="form-horizontal"  (ngSubmit)="onSubmit()">

      <div class="form-group">
          <label class=" col-sm-1" style="font-weight: bold;" for="name">Name:</label>
          <div class="col-sm-6">
            <input class="form-control" id="name" placeholder="Enter name" [required]="true" formControlName="name">
          </div>
      </div>
      <div class="form-group">
          <label class="col-sm-1" style="font-weight: bold;" for="description">Description:</label>
          <div class="col-sm-6">
            <textarea class="form-control" type="text" id="description" placeholder="Enter description" [required]="true" formControlName="description"></textarea>
          </div>
      </div>
      <br>
      <div class="form-group">
        <div class="col-sm-offset-1 col-sm-6">
          <button style="margin-right:1em;" type="submit" [disabled]="!editInventoryTypeForm.valid" class="btn btn-sm btn-success" (click)="onSubmit()">Update</button>
          <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Cancel</button>
        </div>
      </div>
    </form>
    </div>
  </div>
    </div>
    </div>
  </div>
