import { Component, ViewChild } from '@angular/core';
import { MatPaginator } from '@angular/material/paginator';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatSort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
import { SupplierService } from 'src/app/service/supplier.service';
import { Supplier } from 'src/app/shared/supplier';
import { MatIcon } from '@angular/material/icon';

@Component({
  selector: 'app-view-suppliers',
  templateUrl: './view-suppliers.component.html',
  styleUrls: ['./view-suppliers.component.css']
})
export class ViewSuppliersComponent {

  suppliers: Supplier[] = []
  filteredsuppliers: Supplier[] = [];
  dataSource: Supplier[]= [];
  // displayedColumns: string[] = ['supplierID','supplierName', 'supplierType', 'supplierEmail', 'supplierNumber', 'supplierAddress'];
  // dataSource = new MatTableDataSource<any>(this.suppliers);

  constructor(private supplierservice: SupplierService, private snackBar: MatSnackBar){

 }
  @ViewChild(MatPaginator) paginator!: MatPaginator;
  @ViewChild(MatSort) sort = new MatSort();

//  ngAfterViewInit() {
//     this.dataSource.paginator = this.paginator;
//     this.dataSource.sort = this.sort;
//   }

//  applyFilter(event: Event) {
//     const filterValue = (event.target as HTMLInputElement).value;
//     this.dataSource.filter = filterValue.trim().toLowerCase();
//   }



applyFilter(event: Event) {
  const filterValue = (event.target as HTMLInputElement).value.toLowerCase();

  const filteredData = this.suppliers.filter(item => {
    // Replace 'column1', 'column2', etc. with your actual column names
    const column2Value = item.supplierTypeName;
    const column3Value = item.supplierName.toLowerCase();
    // Add more columns if needed

    // Check if any of the columns contain the filter value
    return column2Value.includes(filterValue) || column3Value.includes(filterValue);
  });

  // Update the data source with filtered data
  this.filteredsuppliers = filteredData;
}


  ngOnInit(): void {
    this.GetAllSuppliers()
    console.log(this.suppliers)

  }

  // GetAllSuppliers()
  // {
  //   this.supplierservice.GetAllSuppliers().subscribe(result => {
  //     let supplierList:any[] = result
  //     supplierList.forEach((element) => {
  //       this.suppliers.push(element)

  //     });
  //     this.dataSource = new MatTableDataSource<any>(this.suppliers);
  //   })
  // }

  GetAllSuppliers()
  {
    this.supplierservice.GetAllSuppliers().subscribe(result => {
      let supplierList:any[] = result
      supplierList.forEach((element) => {
        this.suppliers.push(element)

      });
    })
  }

}
