import { Component,OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from 'src/app/service/data.Service';
import { Employee_Role } from 'src/app/shared/EmployeeRole';
import { MatSnackBar, MatSnackBarRef } from '@angular/material/snack-bar';

@Component({
  selector: 'app-add-employee-role',
  templateUrl: './add-employee-role.component.html',
  styleUrls: ['./add-employee-role.component.css']
})
export class AddEmployeeRoleComponent implements OnInit {


  
    constructor(private dataService: DataService, private router: Router, 
      private activated: ActivatedRoute,
      private snackBar: MatSnackBar) {}

     employeeroleForm: FormGroup = new FormGroup({
      name: new FormControl('',[Validators.required]),
      description: new FormControl('',[Validators.required])
     })
  
    ngOnInit(): void {
    }
  
    cancel(){
      this.router.navigate(['/employee-role'])
    }
  
    onSubmit(){

      let employeeRole = new Employee_Role();
      employeeRole.name = this.employeeroleForm.value.name;
      employeeRole.description = this.employeeroleForm.value.description;
     
       this.dataService.AddEmployeeRole(employeeRole).subscribe(result => {
        this.router.navigate(['/employee-role'])
       });

       this.showSuccessMessage('Employee Role Added successfully');

    }
    // Success notification
    showSuccessMessage(message:string) : void {
      const snackBarRef: MatSnackBarRef<any> = this.snackBar.open(message, 'Ok', {
        duration: 3000, // Duration in milliseconds
        horizontalPosition: 'center',
        verticalPosition: 'bottom'
      });
    }
}
<div class="container-fluid">
  <div class="row">
      <div class="col-md-2">
          <app-navbar></app-navbar>
      </div>
  <div class="col-md-10 order-md-1">
<h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Create Employee Role</h4>
<hr>
<div class="container">
    <div class="row">
        <form [formGroup]="employeeroleForm" class="form-horizontal"  (ngSubmit)="onSubmit()">
            <div class="form-group">
                <label class=" col-sm-1" style="font-weight: bold;" for="name">Name:</label>
                <div class="col-sm-6">
                  <input class="form-control" id="name" placeholder="Enter name" [required]="true" formControlName="name">
                </div>
            </div>

            <div class="form-group">
                <label class="col-sm-1" style="font-weight: bold;" for="description">Description:</label>
                <div class="col-sm-6">
                    <textarea class="form-control" type="text" id="description" placeholder="Provide description" [required]="true" formControlName="description"></textarea>
                </div>
            </div>
            <br>
            <div class="form-group">
              <div class="col-sm-offset-1 col-sm-6">
                <button style="margin-right:1em;" type="submit" [disabled]="!employeeroleForm.valid" class="btn btn-sm btn-success">Add</button>
                <button type="button" class="btn btn-sm btn-warning" (click)="cancel()">Cancel</button>
              </div>
            </div>
          </form>
    </div>
</div>
  </div>
  </div>
</div>
