import { Component } from '@angular/core';

@Component({
  selector: 'app-add-help',
  templateUrl: './add-help.component.html',
  styleUrls: ['./add-help.component.css']
})
export class AddHelpComponent {
  password: string = '';
  showPassword: boolean = false;

  togglePasswordVisibility() {
    this.showPassword = !this.showPassword;
  }

}
<div>
    <input type="password" [(ngModel)]="password" placeholder="Enter Password">
    <input type="checkbox" (change)="togglePasswordVisibility()"> Show Password
  </div>

  <div>
    <input [type]="showPassword ? 'text' : 'password'" [(ngModel)]="password" placeholder="Enter Password">
    <input type="checkbox" (change)="togglePasswordVisibility()"> Show Password
  </div>
