import { Component, OnInit } from '@angular/core';
import { DataService } from 'src/app/service/data.Service';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar, MatSnackBarRef } from '@angular/material/snack-bar';
import { Entertainment_Type } from 'src/app/shared/entertainmentType';


@Component({
  selector: 'app-add-entertainment-type',
  templateUrl: './add-entertainment-type.component.html',
  styleUrls: ['./add-entertainment-type.component.css'],
  styles: [`
.message {
  padding: 10px;
  background-color: lightgray;
  margin-top: 10px;
}
`]
})
export class AddEntertainmentTypeComponent implements OnInit {

  constructor(private router: Router, private dialog: MatDialog, private snackBar: MatSnackBar, private dataService: DataService){}

  ngOnInit(): void {

  }


  entertainmentform : FormGroup = new FormGroup({
    Name: new FormControl('',[Validators.required]),
    Description: new FormControl('',[Validators.required])

  });

  cancel(){
    this.router.navigate(['/entertainment-types'])
  }

  onSubmit() {
    if (this.entertainmentform.invalid) {
      return;
    }

    let entertainmentType = new Entertainment_Type();
    entertainmentType.name= this.entertainmentform.value.surname;
    entertainmentType.description = this.entertainmentform.value.firstName;


    this.dataService.AddEntertainmentType(entertainmentType).subscribe(result => {
      this.router.navigate(['/entertainment-types'])
    });

    this.snackBar.open(
      this.entertainmentform.get('Name')!.value + ` created successfully`,
      'X',
      { duration: 5000 }
    );
  }
}
<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-2">
  <h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;"> Add Entertainment</h4>
  <hr>

   <div class="container">
     <div class="row">
        <form [formGroup]="entertainmentform" class="form-horizontal" (ngSubmit)="onSubmit()">
            <div class="form-group">
                <label class=" col-md-1" style="font-weight: bold;" for="Name">Entertainment:</label>
                <div class="col-md-6">
                  <input class="form-control" placeholder="Enter entertainment type" [required]="true" name="Name" formControlName="Name">
                </div>
            </div>

            <div class="form-group">
                <label class="col-sm-1" style="font-weight: bold;" for="Description">Description:</label>
                <div class="col-sm-6">
                    <textarea class="form-control" type="text" id="Description" placeholder="Provide description" [required]="true" formControlName="Description"></textarea>
                </div>
            </div>
            <br>
            <div class="form-group">
                <div class="col-sm-offset-1 col-sm-6">
                    <button style="margin-right:1em;" type="submit" [disabled]="!entertainmentform.valid" class="btn btn-sm btn-success">Create</button>
                    <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Discard</button>
                  </div>
            </div>
        </form>
     </div>
   </div>



</div>
