import { EmployeeService } from '../services/employee.service';




@Component({
  selector: 'app-add-employee',
  templateUrl: './add-employee.component.html',
  styleUrls: ['./add-employee.component.css']
})
export class AddEmployeeComponent implements OnInit {

   employeeForm = new FormGroup({
     surname: new FormControl(''),
     firstName: new FormControl(''),
     email_Address: new FormControl(''),
     physical_Address: new FormControl(''),
     phoneNumber: new FormControl('')

   })

  constructor(private employeeservice: EmployeeService, private router: Router) { }

  ngOnInit(): void {

  }

  cancel(){
    this.router.navigate(['/home'])
  }

     onSubmit(){

      let employee = new Employee();
      employee.name = this.employeeForm.value.surname;
      employee.description = this.employeeForm.value.firstName;
      employee.duration = this.employee.value.duration;
         this.employeeservice.AddEmployee(this.employeeForm.value).subscribe(result => {
               this.router.navigate(['/view-employees'])
         })
       }

}
