import { Component, Inject, OnInit } from '@angular/core';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { DataService } from 'src/app/service/data.Service';
import { BookingEvent } from 'src/app/shared/bookingevent';
import { Router } from '@angular/router';
import { MatSnackBar } from '@angular/material/snack-bar';




@Component({
  selector: 'app-add-schedule',
  templateUrl: './add-schedule.component.html',
  styleUrls: ['./add-schedule.component.css']
})

export class AddScheduleComponent implements OnInit{

  formData = new FormData();
  bookingevents: BookingEvent[]=[];




  constructor( private dataService: DataService, private fb: FormBuilder,private router: Router, 
    private snackBar: MatSnackBar,
    public dialogRef: MatDialogRef<AddScheduleComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {

  }

    scheduleform: FormGroup = this.fb.group({
      title: ['', Validators.required],
      start_time: ['', Validators.required],
      end_time: ['', Validators.required],
      event: [null, Validators.required],
    })

    ngOnInit(): void {
      this.GetAllEvents()
    }

  //Events method
  GetAllEvents()
  {
    this.dataService.GetAllEvents().subscribe(result => {
      let eventsList:any[] = result
      eventsList.forEach((element) => {
        this.bookingevents.push(element)

      });
    })
  }

   onSave(): void {
     if (this.scheduleform.valid) {
         const eventData = this.scheduleform.value;

        // Add any additional logic (e.g., saving to backend) here if needed.
     this.dialogRef.close(eventData);
      }
    }


  clearData(){
    this.formData.delete("title");
    this.formData.delete("start_time");
    this.formData.delete("end_time");
    this.formData.delete("event");

  }

  onCancel(): void {
    this.dialogRef.close();
  }
}
<div mat-subheader>
  <h2 mat-dialog-title>Add Schedule </h2>
<button style="margin-left: 1em;" mat-icon-button mat-dialog-close class="close-button" aria-label="Close">
  <mat-icon>close</mat-icon>
</button>
</div>

<div mat-dialog-content>
  <form [formGroup]="scheduleform">
    <mat-form-field>
      <mat-label>Title</mat-label>
      <input matInput formControlName="title" placeholder="Schedule Title">
      <!-- <mat-error *ngIf="scheduleform.get('title').invalid">Title is required</mat-error> -->
    </mat-form-field>

    <mat-form-field>
        <mat-label>Event</mat-label>
        <mat-select formControlName="event_name" >
            <mat-option *ngFor="let item of bookingevents "  [value]="item.eventId">{{item.event_Name}}</mat-option>
        </mat-select>
    </mat-form-field>

    <mat-form-field>
      <mat-label>Start Time</mat-label>
      <input matInput [matDatepicker]="startDatePicker" formControlName="start" placeholder="Start Time">
      <mat-datepicker-toggle matSuffix [for]="startDatePicker"></mat-datepicker-toggle>
      <mat-datepicker #startDatePicker></mat-datepicker>
    </mat-form-field> 

    <mat-form-field>
      <mat-label>End Time</mat-label>
      <input matInput [matDatepicker]="endDatePicker" formControlName="end" placeholder="End Time">
      <mat-datepicker-toggle matSuffix [for]="endDatePicker"></mat-datepicker-toggle>
      <mat-datepicker #endDatePicker></mat-datepicker>
    </mat-form-field> 



  </form>
</div>
<div mat-dialog-actions>
  <button mat-button (click)="onCancel()">Cancel</button>
  <button mat-button (click)="onSave()">Save</button>
</div>
