import { Component } from '@angular/core';
import { CalendarOptions } from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import { EventInput } from '@fullcalendar/core';



@Component({
  selector: 'app-schedule-display',
  templateUrl: './schedule-display.component.html',
  styleUrls: ['./schedule-display.component.css']
})

export class ScheduleDisplayComponent {

  calendarPlugins = [dayGridPlugin, interactionPlugin];
  events: EventInput[] = [
    // Sample events data (replace with your own data)
    {
      title: 'Event 1',
      date: new Date().toISOString(),
    },
    {
      title: 'Event 2',
      date: new Date().toISOString(),
    },
  ];

  handleEventClick(eventClickInfo: any) {
    // Handle event click here (e.g., show a dialog to edit event)
    console.log('Event clicked:', eventClickInfo);
  }
}
<h3 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-weight: bolder; font-size: 50px;">Schedule</h3>

<div class="container text-center">
<full-calendar  [options]="calendarOptions"></full-calendar>
</div>
