import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ReactiveFormsModule } from '@angular/forms'; // Import the ReactiveFormsModule
import { ActivatedRoute } from '@angular/router';
import { InventoryService } from 'src/app/service/inventory.service';
import { SupplierService } from 'src/app/service/supplier.service';
import { InventoryItem } from 'src/app/shared/inventoryitem';
import { Supplier } from 'src/app/shared/supplier';
import { Router } from '@angular/router';


@Component({
  selector: 'app-receive-order',
  templateUrl: './receive-order.component.html',
  styleUrls: ['./receive-order.component.css']
})
export class ReceiveOrderComponent {

  supplierData: Supplier[] = [];
  inventoryItems: InventoryItem[] = [];
  receiveOrderForm!: FormGroup;
  editInventory: InventoryItem = new InventoryItem();

  constructor(
    private route: ActivatedRoute,
    private supplierservice: SupplierService,
    private inventoryservice: InventoryService,
    private httpClient: HttpClient,
    private fb: FormBuilder,
    private router: Router
  ) {}


  onReceiveOrderClick(itemName: string) {
    this.router.navigate(['/receive-order'], { queryParams: { itemName: itemName } });
  }

  ngOnInit(): void{
    this.receiveOrderForm = this.fb.group({
      itemName: ['', Validators.required],
      supplierId: ['', Validators.required],
      ordered_Date: ['', Validators.required],
      received_Date: [new Date().toISOString().slice(0, 10)],
      ordered_Quantity: ['', [Validators.required, Validators.min(1)]],
    });

    this.route.queryParams.subscribe((params) => {
      const itemName = params['itemName'];
      if (itemName) {
        // Prepopulate the item name in the form
        this.receiveOrderForm.controls['itemName'].setValue(itemName);
        this.getInventoryItemByName(itemName); // Fetch and populate the inventory item name
      }
    });

    this.getAllSuppliers();
  }

  getAllSuppliers(): void {
    this.supplierservice.GetAllSuppliers().subscribe((result) => {
      this.supplierData = result;
    });
  }

  getInventoryItemByName(itemName: string): void {
    this.inventoryservice.GetInventoryItemByName(itemName).subscribe(
      (inventoryItem) => {
        if (inventoryItem) {
          // Set the inventory item name in the form
          this.receiveOrderForm.controls['itemName'].setValue(inventoryItem.itemName);
        } else {
          // Clear the inventory item name if the item name is not found
          this.receiveOrderForm.controls['itemName'].setValue('');
        }
      },
      (error) => {
        // Handle error
        console.error('Error fetching inventory item:', error);
      }
    );
  }

  onSubmit() {
    if (this.receiveOrderForm.valid) {
      const formData = this.receiveOrderForm.value;
      this.httpClient.post('/api/InventoryItem/AddReceivedOrder', formData).subscribe(
        (response: any) => {
          // Handle successful response
          console.log('Order received successfully:', response);
        },
        (error: any) => {
          // Handle error
          console.error('Error receiving order:', error);
        }
      );
    }
  }

}
<h1>Receive Order</h1>
<form [formGroup]="receiveOrderForm" (ngSubmit)="onSubmit()">
  <div>
    <label for="itemName">Enter Inventory Item ID:</label>
    <input type="text" formControlName="itemName">
    <div *ngIf="receiveOrderForm.get('itemName') && receiveOrderForm.get('itemName')">Please enter a valid item ID.</div>
  </div>

  <div>
    <label for="supplierId">Select Supplier:</label>
    <select formControlName="supplierId">
      <option value="">-- Select a supplier --</option>
      <!-- Populate the options with suppliers from the backend -->
      <option *ngFor="let supplier of supplierData" [value]="supplier.supplierId">{{ supplier.supplierName }}</option>
    </select>
    <div *ngIf="receiveOrderForm.get('supplierId')">Please select a supplier.</div>
  </div>

  <div>
    <label for="ordered_Date">Ordered Date:</label>
    <input type="date" formControlName="ordered_Date">
    <div *ngIf="receiveOrderForm.get('ordered_Date')">Please enter the ordered date.</div>
  </div>

  <div>
    <label for="received_Date">Received Date:</label>
    <input type="date" formControlName="received_Date" [readonly]="true">
  </div>

  <div>
    <label for="ordered_Quantity">Ordered Quantity:</label>
    <input type="number" formControlName="ordered_Quantity" min="1">
    <div *ngIf="receiveOrderForm.get('ordered_Quantity')">Please enter a valid quantity (minimum 1).</div>
  </div>

  <button type="submit" [disabled]="receiveOrderForm.invalid">Receive Order</button>
</form>
