  <h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Edit Employee</h4>
  <hr>
  <div class="container">
  <div class="row">  
  <form [formGroup]="updateSupplierForm" class="form-horizontal"  (ngSubmit)="updateSupplier()">
      <div class="form-group">
          <label class=" col-sm-1" style="font-weight: bold;" for="surname">Surname:</label>
          <div class="col-sm-6">
            <input class="form-control" id="surname" placeholder="Enter Surname" [required]="true" formControlName="surname">
          </div>
      </div>
      <div class="form-group">
          <label class="col-sm-1" style="font-weight: bold;" for="firstName">FirstName:</label>
          <div class="col-sm-6">
            <input class="form-control" id="firstName" placeholder="Enter First Name" [required]="true" formControlName="firstName">
          </div>
      </div>
      <div class="form-group">
        <label class="col-sm-1" style="font-weight: bold;" for="email_Address">Email Address:</label>
        <div class="col-sm-6">
          <textarea class="form-control" type="text" id="email_Address" placeholder="Enter Address" [required]="true" formControlName="email_Address"></textarea>
        </div>
    </div>
      <div class="form-group">
          <label class="col-sm-1" style="font-weight: bold;" for="physical_Address">Address:</label>
          <div class="col-sm-6">
            <textarea class="form-control" type="text" id="physical_Address" placeholder="Enter Address" [required]="true" formControlName="physical_Address"></textarea>
          </div>
      </div>
      <div class="form-group">
          <label class="col-sm-1" style="font-weight: bold;" for="phoneNumber">Email:</label>
          <div class="col-sm-6">
            <textarea class="form-control" type="text" id="phoneNumber" placeholder="Enter 10-digit number" [required]="true" formControlName="phoneNumber"></textarea>
          </div>
      </div>
      <div class="form-group">
        <div class="col-sm-offset-1 col-sm-6">
          <button style="margin-right:1em;" type="submit" [disabled]="!updateSupplierForm.valid" class="btn btn-sm btn-success">Confirm</button>
          <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Cancel</button>
        </div>
      </div>
    </form>
    </div>
  </div> 
