import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { EmployeeService } from '../../Employees/services/employee.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-help',
  templateUrl: './add-help.component.html',
  styleUrls: ['./add-help.component.css']
})
export class AddHelpComponent {
  constructor(private employeeservice: EmployeeService, private router: Router) { }

  helpForm: FormGroup = new FormGroup({
    name: new FormControl('',[Validators.required]),
    description: new FormControl('',[Validators.required]),


  })

 ngOnInit(): void {

 }

 cancel(){
   this.router.navigate(['/home'])
 }

    onSubmit(){

     let help = new Help();
     help.name = this.helpForm.value.name;
     help.description = this.helpForm.value.description;
         this.employeeservice.AddEmployee(help).subscribe(result => {
               this.router.navigate(['/view-help'])
         })

  this.employeeservice.addHelp(help).subscribe((res:any) => {

   if(res.statusCode == 200)
   {
     this.router.navigate(['/'])
   }
   else
   {
     alert(res.message);
   }
  });
      }

}

<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-1">
  <h4>Add Employee</h4>
  <hr>
  <div class="container">
  <div class="row">  
  <form [formGroup]="helpForm" class="form-horizontal"  (ngSubmit)="onSubmit()">
      <div class="form-group">
          <label class=" col-sm-1" for="name">Question:</label>
          <div class="col-sm-6">
            <input class="form-control" placeholder="Enter Question" [required]="true" name="name" formControlName="name">
          </div>
      </div>
      <div class="form-group">
          <label class="col-sm-1" for="description">Answer:</label>
          <div class="col-sm-6">
            <input class="form-control"  placeholder="Enter Answer" [required]="true" name="description" formControlName="description">
          </div>
      </div>
      <div class="form-group">
        <div class="col-sm-offset-1 col-sm-6">
          <button style="margin-right:1em;" type="submit" [disabled]="!helpForm.valid" class="btn btn-sm btn-success">Add</button>
          <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Cancel</button>
        </div>
      </div>
    </form>
    </div>
  </div> 
    </div>
    </div>
  </div>
