import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { InventoryService } from 'src/app/service/inventory.service';
import { InventoryItem } from 'src/app/shared/inventoryitem';

@Component({
  selector: 'app-edit-inventoryitem',
  templateUrl: './edit-inventoryitem.component.html',
  styleUrls: ['./edit-inventoryitem.component.css']
})
export class EditInventoryitemComponent {

  constructor(private inventoryservice: InventoryService, 
    private router: Router, 
    private activated:ActivatedRoute) { }

    editInventoryItem: InventoryItem = new InventoryItem();

    editInventoryItemForm: FormGroup = new FormGroup({
       itemName: new FormControl('',[Validators.required]),
       description: new FormControl('',[Validators.required])
    })

    ngOnInit(): void {
      this.activated.params.subscribe(params =>{
      this.inventoryservice.GetInventoryItem(params['id']).subscribe(result =>{
        this.editInventoryItem = result as InventoryItem;
        this.editInventoryItemForm.controls['itemName'].setValue(this.editInventoryItem.itemName);
        this.editInventoryItemForm.controls['description'].setValue(this.editInventoryItem.description);
      })
      })

    }

    cancel(){
      this.router.navigate(['/view-inventoryitems/ :typeId'])
    }

    onSubmit(){
      let inventoryitem = new InventoryItem();
      inventoryitem.itemName = this.editInventoryItemForm.value.itemName;
      inventoryitem.description = this.editInventoryItemForm.value.description;

      this.inventoryservice.EditInventoryItem(this.editInventoryItem.inventory_ItemId, inventoryitem).subscribe((result:any) => {

      this.router.navigate(['view-inventoryitems /: typeId'])
      });
    }
}
<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-1">
  <h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Update Inventory Item</h4>
  <hr>
  <div class="container">
  <div class="row">  
  <form [formGroup]="editInventoryItemForm" class="form-horizontal"  (ngSubmit)="onSubmit()">

      <div class="form-group">
          <label class=" col-sm-1" style="font-weight: bold;" for="itemName">Name:</label>
          <div class="col-sm-6">
            <input class="form-control" id="itemName" placeholder="Enter name" [required]="true" formControlName="itemName">
          </div>
      </div>
      <div class="form-group">
          <label class="col-sm-1" style="font-weight: bold;" for="description">Description:</label>
          <div class="col-sm-6">
            <textarea class="form-control" type="text" id="description" placeholder="Enter description" [required]="true" formControlName="description"></textarea>
          </div>
      </div>
      <br>
      <div class="form-group">
        <div class="col-sm-offset-1 col-sm-6">
          <button style="margin-right:1em;" type="submit" [disabled]="!editInventoryItemForm.valid" class="btn btn-sm btn-success" (click)="onSubmit()">Update</button>
          <button type="button" class="btn btn-sm btn-danger" (click)="cancel()">Cancel</button>
        </div>
      </div>
    </form>
    </div>
  </div>
    </div>
    </div>
  </div>
