import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { DataService } from 'src/app/service/data.Service';
import { BookingEvent } from 'src/app/shared/bookingevent';
import { Router } from '@angular/router';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Schedule } from 'src/app/shared/schedule';


@Component({
  selector: 'app-add-schedule',
  templateUrl: './add-schedule.component.html',
  styleUrls: ['./add-schedule.component.css']
})

export class AddScheduleComponent implements OnInit{

  formData = new FormData();
  bookingevents: BookingEvent[]=[];
  newEvents: Event[] = [];
  dateStr: string = ''; 

  constructor( private dataService: DataService, private fb: FormBuilder,private router: Router, 
    private snackBar: MatSnackBar,
  ) {}

    scheduleform: FormGroup = this.fb.group({
      title: ['', Validators.required],
      start_time: [null, Validators.required],
      end_time: [null, Validators.required],
      event: [null, Validators.required],
      description:['',Validators.required]
    })

    ngOnInit(): void {
      this.GetAllEvents()
    }

  //Events method
  GetAllEvents()
  {
    this.dataService.GetAllEvents().subscribe(result => {
      let eventsList:any[] = result
      eventsList.forEach((element) => {
        this.bookingevents.push(element)

      });
    })
  }

   onSave(): void {
     if (this.scheduleform.valid) {
        const eventData = this.scheduleform.value;
        eventData.dateStr = this.dateStr;

        // Add any additional logic (e.g., saving to backend) here if needed.

       //this.dialogRef.close(eventData);
      }

    }


  clearData(){
    this.formData.delete("title");
    this.formData.delete("start_time");
    this.formData.delete("end_time");
    this.formData.delete("event");

  }

   onCancel(): void {

   }
}
<!-- Bootstrap Modal -->
<div class="modal" tabindex="-1" role="dialog" #addScheduleModal>
  <div class="modal-dialog modal-dialog-centered" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Add Schedule</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <!-- Your form content here -->
        <form [formGroup]="scheduleform">
          <div class="form-group">
            <label>Title</label>
            <input type="text" class="form-control" formControlName="title" placeholder="Schedule Title">
          </div>

          <div class="form-group">
            <label>Event</label>
            <select class="form-control" formControlName="event">
              <option *ngFor="let item of bookingevents" [value]="item.eventId">{{item.event_Name}}</option>
            </select>
          </div>

          <div class="form-group">
            <label>Start Time</label>
            <div class="input-group date" id="startDatePicker" data-target-input="nearest">
              <input type="text" class="form-control datetimepicker-input" formControlName="start_time"
                data-target="#startDatePicker" placeholder="Start Time" />
              <div class="input-group-append" data-target="#startDatePicker" data-toggle="datetimepicker">
                <div class="input-group-text"><i class="far fa-calendar"></i></div>
              </div>
            </div>
          </div>

          <div class="form-group">
            <label>End Time</label>
            <div class="input-group date" id="endDatePicker" data-target-input="nearest">
              <input type="text" class="form-control datetimepicker-input" formControlName="end_time"
                data-target="#endDatePicker" placeholder="End Time" />
              <div class="input-group-append" data-target="#endDatePicker" data-toggle="datetimepicker">
                <div class="input-group-text"><i class="far fa-calendar"></i></div>
              </div>
            </div>
          </div>

          <div class="form-group">
            <label>Description</label>
            <input type="text" class="form-control" formControlName="description" />
          </div>
        </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal" (click)="onCancel()">Cancel</button>
        <button type="button" class="btn btn-primary" (click)="onSave()">Save</button>
      </div>
    </div>
  </div>
</div>
