import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { MatSnackBar, MatSnackBarRef } from '@angular/material/snack-bar';
import { ActivatedRoute, Router } from '@angular/router';
import { InventoryService } from 'src/app/service/inventory.service';
import { InventoryType } from 'src/app/shared/inventorytype';

@Component({
  selector: 'app-add-inventorytypes',
  templateUrl: './add-inventorytypes.component.html',
  styleUrls: ['./add-inventorytypes.component.css']
})
export class AddInventorytypesComponent {

  inventorytypes:InventoryType[] = []

  constructor(private inventoryservice: InventoryService, private router: Router, 
    private activated: ActivatedRoute,
    private snackBar: MatSnackBar) {}

    inventoryTypeForm: FormGroup = new FormGroup({
      name: new FormControl('',[Validators.required]),
      description: new FormControl('',[Validators.required])
     })

     ngOnInit(): void {
    }

    cancel(){
      this.router.navigate(['/view-inventorytypes'])
    }

    onSubmit(){

      let inventorytype = new InventoryType();
      inventorytype.name = this.inventoryTypeForm.value.name;
      inventorytype.description = this.inventoryTypeForm.value.description;

       this.inventoryservice.AddInventoryType(inventorytype).subscribe(result => {
        this.router.navigate(['/view-inventorytypes'])
       });

       this.showSuccessMessage('Inventory Type Added successfully');

    }
    // Success notification
    showSuccessMessage(message:string) : void {
      const snackBarRef: MatSnackBarRef<any> = this.snackBar.open(message, 'Ok', {
        duration: 3000, // Duration in milliseconds
        horizontalPosition: 'center',
        verticalPosition: 'bottom'
      });
    }

}
<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-1">
  <h4 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-size: 50px;">Create Inventory Type</h4>
  <hr>
  <div class="container">
      <div class="row">
          <form [formGroup]="inventoryTypeForm" class="form-horizontal"  (ngSubmit)="onSubmit()">
              <div class="form-group">
                  <label class=" col-sm-1" style="font-weight: bold;" for="name">Name:</label>
                  <div class="col-sm-6">
                    <input class="form-control" id="name" placeholder="Enter Supplier Type Name" [required]="true" formControlName="name">
                  </div>
              </div>

              <div class="form-group">
                  <label class="col-sm-1" style="font-weight: bold;" for="description">Description:</label>
                  <div class="col-sm-6">
                      <textarea class="form-control" type="text" id="description" placeholder="Provide description" [required]="true" formControlName="description"></textarea>
                  </div>
              </div>
              <br>
              <div class="form-group">
                <div class="col-sm-offset-1 col-sm-6">
                  <button style="margin-right:1em;" type="submit" [disabled]="!inventoryTypeForm.valid" class="btn btn-sm btn-success">Add</button>
                  <button type="button" class="btn btn-sm btn-warning" (click)="cancel()">Cancel</button>
                </div>
              </div>
            </form>
      </div>
  </div>
    </div>
    </div>
  </div>
