import { Component,OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { DataService } from 'src/app/service/data.Service';
import { Employee_Role } from 'src/app/shared/EmployeeRole';
import { HttpClient } from '@angular/common/http';
import { MatSnackBar } from '@angular/material/snack-bar';


@Component({
  selector: 'app-employee-role',
  templateUrl: './employee-role.component.html',
  styleUrls: ['./employee-role.component.css'],
  template: '<button (click)="deleteItem()">Delete</button>'
})
export class EmployeeRoleComponent implements OnInit{

employeeroles:Employee_Role[] = []

constructor(private dataService: DataService, 
  private router: Router, 
  private http:HttpClient,
  private snackBar: MatSnackBar) { }

  // message box
  deleteItem(): void{
 
    const confirmationSnackBar = this.snackBar.open('Are you sure you want to delete the employee role?','Delete',{
        duration: 5000,
      });
     
      confirmationSnackBar.onAction().subscribe(() => {
        this.deleteItemFromServer();
       window.location.reload();
      })

  }

  deleteItemFromServer(): void{
    this.DeleteEmployeeRole;
  }


  ngOnInit():void {
  this.GetAllEmployeeRoles()
  }
  // Get all employees method
  GetAllEmployeeRoles()
  {
    this.dataService.GetAllEmployeeRoles().subscribe(result => {
      let employeeRoleList:any[] = result
      employeeRoleList.forEach((element) => {
        this.employeeroles.push(element)
      });
    })
  }
 // Delete Employee role method
  DeleteEmployeeRole(employee_RoleId: Number){
    this.dataService.DeleteEmployeeRole(employee_RoleId).subscribe(result => {
      this.deleteItem();
      });
    }   
    
    searchTerm: string = '';

    

    // search(searchTerm: string) {
    //   this.searchClicked.emit(searchTerm);
    // }
}
<div class="container-fluid">
    <div class="row">
        <div class="col-md-2">
            <app-navbar></app-navbar>
        </div>
    <div class="col-md-10 order-md-1">
        
<h3 style="text-align: center; font-family: 'Times New Roman', Times, serif; font-weight: bolder; font-size: 50px;">Employee Roles</h3>
<div class="input-group">
<input type="text" class="form-control" placeholder="Search">
<button class="btn">
    <i class="fa fa-search" aria-hidden="true"></i>
  </button>
  <div class="addButton">
  <button style="margin-left: 1em;" type="button" class="btn btn-sm btn-success" [routerLink]="['/add-employee-role', employeeroles]">Create</button>
  </div>
</div>




<table class="table table-striped table-hover outlined-table table-container"> 
    <thead class="tableColor"> 
        <tr> 
            <th scope="col">Employee Role ID</th> 
            <th scope="col">Employee Role</th> 
            <th scope="col">Description</th>
            <th scope="col"></th>
            </tr> 
    </thead> 
    <tbody> 
        <tr *ngFor="let EmployeeRole of employeeroles"> 
            <td>{{EmployeeRole.employee_RoleId}}</td> 
            <td>{{EmployeeRole.name}}</td> 
            <td>{{EmployeeRole.description}}</td> 
            <td>
                <button style="margin-right: 1em;" type="button" [routerLink]="['/edit-employee-role',EmployeeRole.employee_RoleId]" type class="btn btn-sm btn-info"><i class="fa fa-pencil" aria-hidden="true"></i> Edit</button>
                <button type="button" class="btn btn-sm btn-danger" (click)="DeleteEmployeeRole(EmployeeRole.employee_RoleId)"><i class="fa fa-trash" aria-hidden="true"></i> Delete</button>
            </td>
        </tr> 
    </tbody> 
</table>
    </div>
    </div>
</div>
