import { HttpClient } from '@angular/common/http';
import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { MatSnackBar } from '@angular/material/snack-bar';
import { DataService } from 'src/app/service/data.Service';
import { FoodType } from 'src/app/shared/food-type';

@Component({
  selector: 'app-food-type',
  templateUrl: './food-type.component.html',
  styleUrls: ['./food-type.component.css']
})

export class FoodTypeComponent {
  foodType: FoodType[] = []
  searchQuery!: string;

  constructor(private dataService: DataService, 
    private router: Router, 
    private httpClient: HttpClient, 
    private snackbar: MatSnackBar) {}

  deleteItem(): void {
    const confirmationSnackBar = this.snackbar.open('Are you sure you want to delete this item?', 'Cancel Delete', {duration: 5000,})
    
    confirmationSnackBar.onAction().subscribe(() => {
      this.deleteItemFromServer();
      window.location.reload();
    });
  }

  ngOnInit(): void {
    this.GetAllFoodTypes();
  }

  deleteItemFromServer(): void {
    this.DeleteFoodType;
  }

 


  GetAllFoodTypes()
  {
    this.dataService.GetAllFoodTypes().subscribe(result => {
      let foodTypesList: any[] = result
      foodTypesList.forEach((element) => {
        this.foodType.push(element)
      });
    })
  
  }

  DeleteFoodType(foodTypeId: number)
  {
    this.dataService.DeleteFoodType(foodTypeId).subscribe(result => {
        this.deleteItem();
      });
  }

  /*searchQuery: string;

  search(searchQuery: string)
  {
      this.dataService.search(this.searchQuery).subscribe(
        (results) => {
          this.foodType = results;
        },
        (error) => {
          console.log(error);
        }
      )
  }*/

}

<div class="container-fluid">
    <!-- navbar -->
    <div class="row">
            <div class="col-md-2">
                <app-navbar></app-navbar>
            </div>
    
    
        <div class="col-md-10">
            <!-- heading -->
            <div>
                <h1 class="heading">Food Types</h1>
            </div>
        
            <div class="container-fluid">
                <!-- search bar-->
                <div class="input-group">
                    <input type="text" class="form-control" placeholder="Search food type"> <!-- [(ngModel)]="searchQuery" (input)="search()"-->
                    <button class="btn" onclick="search(searchTerm)">
                        <i class="fa fa-search" aria-hidden="true">  Search</i>
                    </button>

                <span style="align-content: right;">
                    <button style="margin-left: 1em;" type="button" class="btn btn-sm btn-success" [routerLink]="'/create-food-type'">New</button>
                </span>

                <!--table-->
                <table class="table table-striped table-container outlined-table">
                    <thead class="tableColor">
                        <tr>
                            <th scope="col">No</th>
                            <th scope="col">Name</th>
                            <th scope="col">Description</th>
                            <th scope="col"></th>
                            
                        </tr>
                    </thead>
                    <tbody>
                        <tr *ngFor="let item of foodType"> 
                            <td> {{ item.foodTypeId }} </td>
                            <td> {{ item.name }} </td>
                            <td> {{ item.description }} </td>
                            <td>
                            <!-- edit and delete buttons -->
                            <button style="margin-right: 1em;" type="button" [routerLink]="['/edit-food-type', item.foodTypeId]" type class="btn btn-sm btn-info"><i class="fa fa-pencil" aria-hidden="true"></i> Edit</button>
                            <button type="button" class="btn btn-sm btn-danger" (click)="DeleteFoodType(item.foodTypeId)"><i class="fa fa-trash" aria-hidden="true"></i> Delete</button>
                          </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</div>
